            top: -3px;
        }

        .cropper-point.point-sw {
            bottom: -3px;
            cursor: nesw-resize;
            left: -3px;
        }

        .cropper-point.point-se {
            bottom: -3px;
            cursor: nwse-resize;
            height: 20px;
            opacity: 1;
            right: -3px;
            width: 20px;
        }

        @media (min-width: 768px) {
            .cropper-point.point-se {
                height: 15px;
                width: 15px;
            }
        }

        @media (min-width: 992px) {
