        }

        @media (min-width: 1200px) {
            .cropper-point.point-se {
                height: 5px;
                opacity: 0.75;
                width: 5px;
            }
        }

        .cropper-point.point-se::before {
            background-color: #39f;
            bottom: -50%;
            content: ' ';
            display: block;
            height: 200%;
            opacity: 0;
            position: absolute;
            right: -50%;
            width: 200%;
        }

        .cropper-invisible {
