        }

        .cropper-hide {
            display: block;
            height: 0;
            position: absolute;
            width: 0;
        }

        .cropper-hidden {
            display: none !important;
        }

        .cropper-move {
            cursor: move;
        }

        .cropper-crop {
            cursor: crosshair;
        }

        .cropper-disabled .cropper-drag-box,
        .cropper-disabled .cropper-face,
        .cropper-disabled .cropper-line,
        .cropper-disabled .cropper-point {
